import React, { ReactNode } from "react";
import ActivityFeedItem, {
  ActivityFeedItemData,
} from "@/wab/client/components/widgets/plasmic/ActivityFeedItem";
import {
  PlasmicActivityFeed__VariantsArgs,
  PlasmicActivityFeed,
} from "@/wab/client/components/widgets/plasmic/PlasmicActivityFeed";

interface ActivityFeedProps {
  items: ActivityFeedItemData[];
  selectedId: string;
  onSelect: (id: string) => void;
  // className prop is required for positioning instances of
  // this Component
  className?: string;
  children?: never;
}

function ActivityFeed(props: ActivityFeedProps) {
  return (
    <PlasmicActivityFeed
      feedContainer={{
        children: props.items.map((e) => (
          <ActivityFeedItem
            key={e.id}
            item={e}
            selected={e.id === props.selectedId}
            onClick={() => {
              props.onSelect(e.id);
            }}
          />
        )),

        // className prop needs to be piped to the root element of this component
        className: props.className,
      }}
    />
  );
}

export default ActivityFeed as React.FunctionComponent<ActivityFeedProps>;
